mapper.select:
SELECT 
FROM SHIZIQIU_CONF_NODE AS t


		t.node_group = #{nodeGroup}


		AND t.node_key like CONCAT('%',#{nodeKey},'%')


ORDER BY t.node_key
LIMIT #{offset}, #{pagesize}

SELECT count(1) FROM SHIZIQIU_CONF_NODE AS t
t.node_group = #{nodeGroup}
AND t.node_key like CONCAT('%',#{nodeKey},'%')

mapper.delete:
DELETE FROM SHIZIQIU_CONF_NODE
WHERE node_group = #{nodeGroup}
	AND node_key = #{nodeKey}

mapper.select:
SELECT 
FROM SHIZIQIU_CONF_NODE AS t
WHERE t.node_group = #{nodeGroup}
	AND t.node_key = #{nodeKey}

mapper.update:
UPDATE SHIZIQIU_CONF_NODE 
SET node_value = #{nodeValue}
	, node_desc = #{nodeDesc}
WHERE node_group = #{nodeGroup}
	AND node_key = #{nodeKey}
